<script>
export default {
    data() {
      return {
        today: this.$common.formatDate(),
        userList: [],
        rechargeList: [],
        withdrawList: [],
        orderList: [],
        todayOrderList: [],
        todayRechargeList: [],
        todayWithdrawList: [],
        todayShouChongList: [],
        total: 0,
        totalRecharge: 0,
        totalWidthdraw: 0,
        todayRecharge: 0,
        todayWithdraw: 0,
        num: 0
      }
    },
    computed: {
        totalLoss () {
            var shengyu = this.totalRecharge - this.totalWidthdraw;
            var loss = (this.total - shengyu).toFixed(2);
            return Number(loss)
        }
    },
    created() {
        this.getUser();
        this.getRecharge();
        this.getWithdraw();
        this.getOrder();
    },
    methods: {
        getUser() {
            this.$http.post('/admin/allUserList').then(response => {
                this.userList = response.list;
                this.total = this.getTotal(response.list);
            }).catch(() => {})
        },
        getRecharge() {
            this.$http.post('/admin/allRechargeList', {status: 2, filterTest: true}).then(response => {
                var resultList = response.list.filter(item => !item.as_type || item.as_type === 1);
                var todayData = this.getTodayData(resultList);
                this.rechargeList = resultList;
                this.totalRecharge = this.getTotal(resultList);
                this.todayRechargeList = this.unique(todayData);
                this.todayShouChongList = this.getTodayShouChong(this.todayRechargeList, resultList);
                this.todayRecharge = this.getTotal(todayData);
            }).catch(() => {})
        },
        getWithdraw() {
            this.$http.post('/admin/allWithdrawList', {status: 2, filterTest: true}).then(response => {
                var resultList = response.list.filter(item => !item.as_type || item.as_type === 1);
                this.withdrawList = resultList;
                this.totalWidthdraw = this.getTotal(resultList);
                var todayData = this.getTodayData(resultList);
                this.todayWithdrawList = this.unique(todayData);
                this.todayWithdraw = this.getTotal(todayData);
            }).catch(() => {})
        },
        getOrder() {
            var date = this.$common.formatDate();
            this.$http.post('/admin/orderList', {pageSize: 1000000, status: 2, startTime: date + ' 00:00:00', endTime: date + ' 23:59:59', filterTest: true}).then(response => {
                this.orderList = response.list;
                this.todayOrderList = this.unique(response.list);
            }).catch(() => {})
        },
        unique(arr) {
            var array = [];
            for (var i = 0; i < arr.length; i++) {
                var filterArr = array.filter(item => item.user_id === arr[i].user_id);
                if (!filterArr.length) {
                    array.push(arr[i])
                }
            }
            return array;
        },
        getTodayData(list) {
            var vm = this;
            var todayData = list.filter(item => {
                var date = this.$common.formatDate(item.create_date);
                if (date == vm.today) {
                    return true
                }
                return false
            })
            return todayData
        },
        getTotal(list) {
            var num = 0;
            for (var i = 0; i < list.length; i++) {
                num += list[i].amount;
            }
            var total = num.toFixed(2);
            return Number(total);
        },
        getTodayShouChong(tList, allList) {
            var arr = [];
            var vm = this;
            for (var i = 0; i < tList.length; i++) {
                var filterList = allList.filter(item => item.user_id === tList[i].user_id);
                if (filterList.length > 1) {
                    var beforToday = filterList.filter(item => {
                        var date = vm.$common.formatDate(item.create_date);
                        return date !== vm.today
                    });
                    if (!beforToday.length) {
                        arr.push(tList[i]);
                    }
                } else {
                    arr.push(tList[i]);
                }
            }
            return arr
        }
    }
   
}
</script>

